import React, { Component } from 'react';

class Login extends Component {
    constructor() {
        super();
        this.state = {username: ''};
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit = (e) => {
        e.preventDefault();
        var self = this;

        fetch('http://localhost:3000', {
            method: 'POST',
            body: {
                username: self.refs.username.value,
                password: self.refs.password.value
            }
        })
        .then((res) => {
            return res.json();
        });
    }

    render() {
        return (
            <div className="container" id="LoginFields">
                <div className="row">
                    <div className="col-md-12">
                        <h4>LOGIN TO MY SITE</h4>
                    </div>
                </div>
                <div class="row">
                    <div className="col-md-8 .offset-md-4 justify-content-center">
                        <div class="input-group mb-3">
                            <div class="input-group-prepend">
                                <span class="input-group-text" id="inputGroup-sizing-default">Email</span>
                            </div>
                            <input type="text" class="form-control" aria-label="Default" aria-describedby="inputGroup-sizing-default"></input>
                        </div>
                    </div>
                </div>
                <div class="row">
                    <div className="col-md-8 .offset-md-4 justify-content-center">
                        <div class="input-group mb-3">
                            <div class="input-group-prepend">
                                <span class="input-group-text" id="inputGroup-sizing-default">Password</span>
                            </div>
                            <input type="text" class="form-control" aria-label="Default" aria-describedby="inputGroup-sizing-default"></input>
                        </div>
                        <button type="button" onClick={this.handleSubmit()} class="btn btn-primary" ref="username">Login</button>
                    </div>
                </div>            
            </div>
        );
    }
}

export default Login;
