import React, { Component } from 'react';

let GRAPHQL_URL = 'http://localhost:5000';

class Register extends Component {
    constructor() {
        super();
        this.state = {name: '', username: '', password1: '', password2: '', email: ''};
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit = (e) => {
        e.preventDefault();
        var self = this;

        if(self.state.password1 === self.state.password2)
            this.setState({errormessage: 'Passwords do not match'})

        console.log(self.state.name);
        console.log(self.state.username);
        console.log(self.state.password);
        console.log(self.state.email);

        //let res = this.sendLoginData(self.state.username, self.state.password);
        //console.log(res);           
    }

    changeHandler = (e)=> {
        let name = e.target.name;
        let val = e.target.value; 

        this.setState({[name]: val});
    } 

    sendLoginData = async (un, pw) => {
        let query = `query login($un: String!, $pw: String!) {
            login(un: $un, pw: $pw)
        }`;

        let response = await fetch(GRAPHQL_URL, {
            method: 'POST',
            headers: {
                'content-type': 'application/json',
                'accept': 'application/json'
            },
            body: JSON.stringify({
                query,
                variables: {un, pw}
            })
        });
        let r = await response.json();
        console.log(r)
    }   

    render() {
        return (
            <div className="container" id="LoginFields">
                <div className="row">
                    <div className="col-md-12">
                        <h4>REGISTER FOR MY SITE</h4>
                    </div>
                </div>
                <div className='RegisterBackground'>
                <form>
                    <input
                        type='text'
                        placeholder='name'
                        name='username'
                        onChange={this.myChangeHandler}
                    />
                    <br />
                    <input
                        type='text'
                        placeholder='username'
                        name='username'
                        onChange={this.myChangeHandler}
                    />
                    <br/>
                     <input
                        type='text'
                        placeholder = 'Email'
                        name='email'
                        onChange={this.myChangeHandler}
                    />
                    <br />         
                    <input
                        type='text'
                        name='password1'
                        placeholder = 'Enter password'
                        onChange={this.myChangeHandler}
                    />
                    <br />
                    <input
                        type='text'
                        placeholder = 'Re-enter password'
                        name='password2'
                        onChange={this.myChangeHandler}
                    />
                    {this.state.errormessage}
                    <br />                              
                </form>
                </div>
                <div className="row">
                    <div className="col-md-7">
                        <button type="button" onClick={this.handleSubmit} className="btn btn-primary">Register</button>
                    </div>
                    <div className="col-md-2">
                        <a className="nav-link" href="/">Back to Login<span class="sr-only">(current)</span></a>
                    </div>
                </div>           
            </div>
        );
    }
}

export default Register;
